import React, { useEffect } from "react";
import Particles from "reac";
const Snowflakes = () => {
  useEffect(() => {
    console.log("Snowflakes component mounted");
  }, []);

  const particlesInit = (main) => {
    console.log(main);
  };

  const particlesLoaded = (container) => {
    console.log(container);
  };

  return (
    <Particles
      id="tsparticles"
      init={particlesInit}
      loaded={particlesLoaded}
      options={{
        particles: {
          number: {
            value: 100,
          },
          size: {
            value: 5,
          },
          move: {
            enable: true,
            speed: 1,
            direction: "bottom",
            random: true,
            straight: false,
          },
          opacity: {
            value: 0.5,
          },
          shape: {
            type: "circle",
          },
        },
        interactivity: {
          events: {
            onClick: {
              enable: true,
              mode: "push",
            },
            onHover: {
              enable: true,
              mode: "repulse",
            },
          },
          modes: {
            push: {
              particles_nb: 4,
            },
            repulse: {
              distance: 200,
              duration: 2,
            },
          },
        },
        retina_detect: true,
      }}
    />
  );
};

export default Snowflakes;
