import React from "react";
import ReactDOM from "react-dom";

export default function Modal({ onClose }) {
  const closeModal = () => {
    onClose();
  };

  return ReactDOM.createPortal(
    <div className="fixed inset-0 block lg:hidden">
      <div
        className="absolute inset-0 bg-black/40 transition-all backdrop-blur-sm"
        onClick={closeModal}
      ></div>
      <div className="overlay absolute w-2/3 h-full bg-white transition-all">
        <div className="flex items-center gap-x-3">
          <img
            className="w-10 h-10"
            src="./image/svg/site-logo.svg"
            alt="Site Logo"
          />
          <h5 className="font-SoraBold text-xl/6 tracking-tighter">AFblog</h5>
        </div>
      </div>
    </div>,
    document.getElementById("modals-parent")
  );
}
