import React, {useContext} from "react";
import ProjectContext from "../../Contexts/ProjectContext";


export default function Header() {

  const contextDeta = useContext(ProjectContext)

  return (
    <div className="container">
      <div className="flex items-center justify-between mt-28 md:mt-28">
        <div className="text-white font-DanaMedium text-2xl md:text-4xl">
          <span className="inline-block mb-5">
            {
              contextDeta.language === "FA" ()
            }
            سلام، خوش اومدی

          </span>
          <p>
            <span className="text-rose-600 font-MorabbaMedium text-3xl md:text-5xl">
              ابوالفضل صادقی
            </span>{" "}
            هستم
          </p>
          <h3 className="mt-5">توسعه دهنده و طراح وب</h3>
        </div>
        <img
          className="hidden lg:block lg:w-96 lg:h-96"
          src="./image/svg/Portfolio Update-bro.svg"
          alt=""
        />
      </div>
    </div>
  );
}
