import React, { useContext } from "react";
import ProjectContext from "../../Contexts/ProjectContext";

export default function Footer() {
  const contextData = useContext(ProjectContext)

  return (
    <footer className={`py-6 bg-black ${contextData.language === "EN" ? 'ltr' : 'rtl'}`}>
      <div className="container">
        <div className="flex items-center justify-between gap-x-20">
          <div className="flex items-center gap-x-3">
            <img
              className="w-10 h-[30px]"
              src="./image/png/AFblog 1.png"
              alt="Site Logo"
            />
            <h4 className="font-SoraBold text-xl/6 text-white tracking-tighter">
              AFblog
            </h4>
          </div>
          <div className="text-zinc-500 font-SoraSemiBold text-base/5 md:text-xs/4 text-wrap">
            All material and intellectual rights belong to Afblog website. Â©
          </div>
        </div>
      </div>
    </footer>
  );
}
