import React, { useContext } from "react";
import ProjectContext from "../../Contexts/ProjectContext";

export default function About() {
  const contextDeta = useContext(ProjectContext);

  return (
    <div
      className={`${
        contextDeta.language === "FA" ? "container rtl" : "container ltr"
      }`}
    >
      <div className="text-white">
        <div>
          <h2>درباره من</h2>
        </div>
        <div>
          <p>تقریبا از بچگی عاشق تکنولوژی و کار با کامپیوتر و موبایل بودم ولی به دلایلی
            رشته تجربی رو انتخاب کردم و دیپلم این رشته رو گرفتم بعد از کنکور هم تغییر رشته دادم و رشته کامپیوتر رو انتخاب کردم
            هدفم بیشتر سمت برنامه نویسی وب بوده یعنی خودم خودخوان برنامه نویسی رو شروع کردم از سن ۱۹ سالگی استارت برنامه نویسی رو زدم

          </p>
        </div>
      </div>
    </div>
  );
}
