import React from "react";
import ReactDOM from "react-dom";

export default function Modal({ onClose }) {
  const closeModal = () => {
    onClose();
  };

  return ReactDOM.createPortal(
    <div className="fixed inset-0 block lg:hidden">
      <div
        className="absolute inset-0 bg-black/40 transition-all backdrop-blur-sm"
        onClick={closeModal}
      ></div>
      <div className="overlay absolute w-2/3 h-full bg-white transition-all px-4">
        <div className="flex items-center justify-between pt-3">
          <div className="flex items-center gap-x-3">
            <img
              className="w-10 h-10"
              src="./image/svg/site-logo.svg"
              alt="Site Logo"
            />
            <h5 className="font-SoraBold text-xl/6 tracking-tighter">AFblog</h5>
          </div>
          <svg
            className="w-6 h-6"
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 20 20"
            fill="currentColor"
            class="size-5"
          >
            <path d="M6.28 5.22a.75.75 0 0 0-1.06 1.06L8.94 10l-3.72 3.72a.75.75 0 1 0 1.06 1.06L10 11.06l3.72 3.72a.75.75 0 1 0 1.06-1.06L11.06 10l3.72-3.72a.75.75 0 0 0-1.06-1.06L10 8.94 6.28 5.22Z" />
          </svg>
        </div>
      </div>
    </div>,
    document.getElementById("modals-parent")
  );
}
